/**
 * Options for canvas screenshot. All optional.
 */
export type CanvasScreenshotOptions = {
    /**
     * Shot YYYY-MM-DD at HH.MM.SS.png] File name.
     */
    filename?: string;
    /**
     * Quality between 0 and 1.
     */
    quality?: number;
    /**
     * Use `canvas.toBlob`.
     */
    useBlob?: boolean;
    /**
     * Automatically download the screenshot.
     */
    download?: boolean;
};
