export type AVProfileItem = {
    name: string;
    /**
     * profile numbers (seq_profile)
     */
    PP: string;
};
export type VCProfileItem = {
    name: string;
    /**
     * profile numbers as hex string
     */
    PP: string;
    /**
     * constraints component as hex string
     */
    CC: string;
};
export type CodecItem = {
    name: string;
    /**
     * the four-character ID for the codec
     */
    cccc: string;
};
export type MediaCodecItem = {
    name: string;
    codec: string;
};
export type VPCodecOptions = {
    /**
     * Human readable codec name: "VP8" or "VP9"
     */
    name: string;
    /**
     * 0, 1, 2 or 3
     */
    profile: number;
    level: string;
    bitDepth: number;
};
export type AVCodecOptions = {
    /**
     * Human readable codec name: "AV1", potentially AV2 in the future
     */
    name: string;
    /**
     * AV profile name: "Main", "High" or "Professional"
     */
    profile: string;
    level: string;
    /**
     * "Main" or "High"
     */
    tier: string;
    /**
     * 8, 10 or 12
     */
    bitDepth: number;
};
export type AVCCodecOptions = {
    /**
     * AVC profile name (eg. "Baseline")
     */
    profile: string;
    level: string;
};
export type HEVCCodecOptions = {
    /**
     * HEVC profile name (eg. "Main 10")
     */
    profile: string;
    compatibility: number;
    level: string;
    /**
     * "Main" or "High"
     */
    tier: string;
    /**
     * TODO
     */
    constraint: string;
};
